import React, { useState } from 'react';
import styled from 'styled-components';

const Container = styled.div`
	width: 30vw;
	display: flex;
	flex-direction: column;
`;

const Group = styled.div`
	position: relative;
	margin: 45px 0;

	.form-input {
		background: none;
		background-color: white;
		color: grey;
		font-size: 18px;
		padding: 10px 10px 10px 5px;
		display: block;
		width: 100%;
		border: none;
		border-radius: 0;
		border-bottom: 1px solid grey;
		margin: 25px 0;

		&:focus {
			outline: none;
		}

		&:focus ~ .form-input-label {
			top: -14px;
			font-size: 12px;
			color: black;
		}
	}

	input[type='password'] {
		letter-spacing: 0.3em;
	}

	.form-input-label {
		color: grey;
		font-size: 16px;
		font-weight: normal;
		position: absolute;
		pointer-events: none;
		left: 5px;
		top: 10px;
		transition: 300ms ease all;

		&.shrink {
			top: -14px;
			font-size: 12px;
			color: black;
		}
	}
`;

const SignInForm = () => {
	const [email, setEmail] = useState('');
	const [password, setPassword] = useState('');

	const handleSubmit = e => e.preventDefault();

	const shrink = value => (value.length ? 'shrink' : '');

	console.log(shrink(password));

	return (
		<Container>
			<h2>I already have an account</h2>
			<span>Sign in with your email and password</span>

			<form onSubmit={handleSubmit}>
				<Group>
					<label
						htmlFor="email"
						className={`${shrink(email)} form-input-label`}
					>
						Email
					</label>

					<input
						className="form-input"
						id="email"
						name="email"
						type="email"
						onChange={e => setEmail(e.target.value)}
						value={email}
						required
					/>
				</Group>
				<Group>
					<label
						htmlFor="password"
						className={`${shrink(password)} form-input-label`}
					>
						Password
					</label>

					<input
						className="form-input"
						id="password"
						name="password"
						type="password"
						onChange={e => setPassword(e.target.value)}
						value={password}
						required
					/>
				</Group>
				<input type="submit" />
			</form>
		</Container>
	);
};

export default SignInForm;
