import { compose } from 'redux';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';

import { selectIsCollectionLoaded } from '../../../redux/directory/selectors';

import Overview from '../../../pages/Shop/Overview';
import Collection from '../../../pages/Shop/Collection';
import WithSpinner from '../../WithSpinner';

const mapStateToProps = createStructuredSelector({
	isLoading: selectIsCollectionLoaded
});

export const OverViewContainer = compose(
	connect(mapStateToProps),
	WithSpinner
)(Overview);

export const CollectionContainer = compose(
	connect(mapStateToProps),
	WithSpinner
)(Collection);
