import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { useSelector } from 'react-redux';

import CollectionItems from '../../../components/CollectionItems';
import Loading from '../../../components/Loading';

const Preview = styled.div`
	display: flex;
	flex-direction: column;
	margin-bottom: 10px;

	.title {
		font-size: 28px;
		margin-bottom: 25px;

		&:hover {
			text-decoration: underline;
		}
	}

	.preview {
		display: flex;
		justify-content: space-between;
	}
`;

const CollectionOverview = () => {
	const data = useSelector(state => state.directory.data);
	const isLoading = useSelector(state => !state.directory.data);

	if (isLoading) {
		return <Loading />;
	}

	const shopData = Object.keys(data).map(key => data[key]);

	return (
		<div>
			{shopData.map(({ items, title, routeName }) => {
				return (
					<Preview key={title}>
						<Link to={`/shop/${routeName}`}>
							<h1 className="title">{title.toUpperCase()}</h1>
						</Link>
						<div className="preview">
							{items.slice(0, 4).map(({ id, ...rest }) => (
								<CollectionItems key={id} id={id} {...rest} />
							))}
						</div>
					</Preview>
				);
			})}
		</div>
	);
};

export default CollectionOverview;
