import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';

import GlobalStyle from './utils/globalStyles';
import Routes from './routing';
import Header from './components/Header';

import { setCurrentUser } from './redux/user/actions';
import { selectCollectionForPreview } from './redux/directory/selectors';

import { auth } from './firebase/utils';
import createUserProfileDocument from './firebase/createUser';
// import addCollectionAndDocs from './firebase/addCollectionAndDocs';

const App = ({ setUser, setCollections }) => {
	useEffect(() => {
		let unsubscribeFromAuth = null;
		unsubscribeFromAuth = auth.onAuthStateChanged(async userAuth => {
			const userRef = await createUserProfileDocument(userAuth);

			if (userAuth) {
				userRef.onSnapshot(snapShot => {
					setUser({
						id: snapShot.id,
						...snapShot.data()
					});
				});
			} else {
				setCurrentUser(userAuth);
			}
		});

		addCollectionAndDocs('collections', setCollections);

		return () => unsubscribeFromAuth();
		// eslint-disable-next-line
	}, []);

	return (
		<div>
			<GlobalStyle />
			<Header />
			<Routes />
		</div>
	);
};

const mapDispatchToProps = createStructuredSelector({
	setUser: setCurrentUser,
	setCollections: selectCollectionForPreview
});

export default connect(null, mapDispatchToProps)(App);
