import React, { useEffect } from 'react';
import { Switch, Route } from 'react-router-dom';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';

import { fetchCollectionsStartAsync } from '../../redux/directory/actions';
import { selectIsCollectionLoaded } from '../../redux/directory/selectors';

import OverviewContainer from '../../components/Collections/Containers/Overview';
import CollectionsContainer from '../../components/Collections/Containers/Collections';

const ShopPage = ({ match, dispatch }) => {
	// eslint-disable-next-line
	useEffect(() => dispatch(fetchCollectionsStartAsync()), []);

	return (
		<Switch>
			<Route exact path={`${match.path}`} component={OverviewContainer} />
			<Route
				path={`${match.path}/:collectionId`}
				component={CollectionsContainer}
			/>
		</Switch>
	);
};

const mapDispatchToProps = dispatch => ({
	fetchCollections: () => dispatch(fetchCollectionsStartAsync())
});

export default connect(null, mapDispatchToProps)(ShopPage);
