import { createGlobalStyle } from 'styled-components';

export default createGlobalStyle`
  body {
    padding: 20px 60px;
    margin: 0;
    font-family: 'Open Sans Condensed', sans-serif;
  }

  a {
    text-decoration: none;
    color: black
  }
`;
