import React from 'react';
import styled from 'styled-components';

const Item = styled.div`
	min-width: 30%;
	height: ${({ size }) => (size ? '380px' : '240px')};
	flex: 1 1 auto;
	display: flex;
	align-items: center;
	justify-content: center;
	border: 1px solid black;
	margin: 0 7.5px 15px;
	background-position: center;
	background-size: cover;

	&:first-child {
		margin-right: 7.5px;
	}

	&:last-child {
		margin-left: 7.5px;
	}

	&:hover {
		cursor: pointer;
	}
`;

const Content = styled.div`
	height: 90px;
	padding: 0 25px;
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
	border: 1px solid black;
	background: white;
	opacity: 0.7;

	&:hover {
		opacity: 0.9;
	}

	.title {
		font-weight: bold;
		margin-bottom: 6px;
		font-size: 22px;
		color: #4a4a4a;
	}

	.subtitle {
		font-weight: lighter;
		font-size: 16px;
	}
`;

const MenuItem = ({ title, imageUrl, size }) => (
	<Item style={{ backgroundImage: `url(${imageUrl})` }} size={size}>
		<Content>
			<div className="title">{title.toUpperCase()}</div>
			<span className="subtitle">SHOP NOW</span>
		</Content>
	</Item>
);

export default MenuItem;
