import React from 'react';
import styled from 'styled-components';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';

import { selectData } from '../../redux/directory/selectors';

import CollectionItems from '../../components/Collections/Items';

const Collection = ({
	match: {
		params: { collectionId }
	},
	shopData
}) => {
	const shopItems = shopData.filter(
		item => item.routeName === collectionId
	)[0].items;
	return (
		<div>
			<h2>{collectionId.toUpperCase()}</h2>
			<div className="preview">
				{shopItems.map(({ id, ...rest }) => (
					<CollectionItems key={id} id={id} {...rest} />
				))}
			</div>
		</div>
	);
};

const mapStateToProps = createStructuredSelector({
	shopData: selectData
});

export default connect(mapStateToProps)(Collection);
