import { firestore } from '../../firebase/utils';
import convertCollectionsToMap from '../../firebase/convertCollectionsToMap';

import directoryTypes from './types';

export const fetchCollectionsStart = () => ({
	type: directoryTypes.FETCH_COLLECTIONS_START
});

export const fetchCollectionsSuccess = collectionsMap => ({
	type: directoryTypes.FETCH_COLLECTIONS_SUCCESS,
	payload: collectionsMap
});

export const fetchCollectionsError = errorMessage => ({
	type: directoryTypes.FETCH_COLLECTIONS_FAILURE,
	payload: errorMessage
});

export const fetchCollectionsStartAsync = () => {
	return dispatch => {
		const collectionRef = firestore.collection('collections');
		dispatch(fetchCollectionsStart());

		collectionRef
			.get()
			.then(snapshot => {
				const dataCollection = convertCollectionsToMap(snapshot);
				console.log(dataCollection, ' <<<<<<<<<<');
				dispatch(fetchCollectionsSuccess(dataCollection));
			})
			.catch(error => {
				dispatch(fetchCollectionsError(error));
			});
	};
};
