import React from 'react';
import styled from 'styled-components';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';

import { toggleCurrentCart } from '../../../redux/cart/actions';
import { selectCartTotalItems } from '../../../redux/cart/selectors';

import { ReactComponent as ShoppingIcon } from '../../../assets/cart.svg';

const Container = styled.div`
	width: 45px;
	height: 45px;
	position: relative;
	display: flex;
	align-items: center;
	justify-content: center;
	cursor: pointer;

	.shopping-icon {
		width: 34px;
		height: 34px;
		margin-bottom: 10px;
	}

	.item-count {
		position: absolute;
		font-size: 14px;
		font-weight: bold;
		bottom: 14px;
	}
`;

const CartIcon = ({ toggleCart, totalItems, dispatch }) => (
	<Container onClick={dispatch(toggleCurrentCart())}>
		<ShoppingIcon className="shopping-icon" />
		<span className="item-count"> {totalItems} </span>
	</Container>
);

const mapStateToProps = createStructuredSelector({
	totalItems: selectCartTotalItems
});

export default connect(mapStateToProps)(CartIcon);
