import React from 'react';
import styled from 'styled-components';

const Container = styled.div`
	width: 100%;
	display: flex;
	min-height: 100px;
	border-bottom: 1px solid darkgrey;
	padding: 15px 0;
	font-size: 20px;
	align-items: center;

	.name,
	.quantity,
	.price {
		width: 23%;
	}

	.remove-button {
		padding-left: 12px;
		cursor: pointer;
		outline: none;
	}

	.quantity {
		width: 23%;
		&::selection {
			color: none;
			background: none;
		}
	}
`;

const Arrow = styled.span`
	padding: 5px;
	cursor: ${({ notAllowed }) => (notAllowed ? 'not-allowed' : 'pointer')}
	color: lightgrey;
	pointer-events: 

	&:hover {
		color: ${({ notAllowed }) => (notAllowed ? 'none' : 'black')};
	}


`;

const Image = styled.div`
	width: 23%;
	padding-right: 15px;

	img {
		width: 100%;
		height: 100%;
	}
`;

const CheckoutItems = ({ cartItem, removeItems, decrease, increase }) => {
	const { name, imageUrl, price, quantity } = cartItem;
	return (
		<Container>
			<Image>
				<img src={imageUrl} alt="item" />
			</Image>
			<span className="name">{name}</span>
			<div className="quantity">
				<Arrow
					onClick={() => decrease(cartItem)}
					notAllowed={quantity === 1}
				>
					&#10094;
				</Arrow>
				{quantity}
				<Arrow onClick={() => increase(cartItem)}>&#10095;</Arrow>
			</div>
			<span className="price">Â£{price}</span>
			<div
				className="remove-button"
				role="button"
				tabIndex={0}
				onClick={() => removeItems(cartItem)}
				onKeyDown={() => {}}
			>
				&#10005;
			</div>
		</Container>
	);
};

export default CheckoutItems;
