import cartTypes from './types';

const INITIAL_STATE = {
	hidden: true,
	cartItems: []
};

const cartReducer = (state = INITIAL_STATE, action) => {
	const calculateQuanity = payload => {
		const newPayload = { ...payload };
		const newItems = state.cartItems.slice();

		const finalArray = state.cartItems.reduce((acc, cur, idx, arr) => {},
		[]);
		// if (
		// 	!state.cartItems.find(
		// 		item =>
		// 			item.id === newPayload.id && item.name === newPayload.name
		// 	)
		// ) {
		// 	newPayload.quantity = 1;
		// 	return [...state.cartItems, newPayload];
		// } else {

		// }

		return [...state.cartItems, newPayload];
	};

	switch (action.type) {
		case cartTypes.TOGGLE_CART_DROPDOWN:
			return {
				...state,
				hidden: !state.hidden
			};
		case cartTypes.ADD_TO_CART:
			return {
				...state,
				cartItems: calculateQuanity(action.payload)
			};
		case cartTypes.REMOVE_FROM_CART:
			return {
				...state,
				cartItems: [
					...state.cartItems.filter(
						item => item.id !== action.payload
					)
				]
			};
		default:
			return state;
	}
};

export default cartReducer;
