import React, { useEffect, useState } from 'react';
import { Switch, Route } from 'react-router-dom';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';
import { firestore } from '../../firebase/utils';
import convertCollectionsToMap from '../../firebase/convertCollectionsToMap';

import {
	setCurrentData,
	fetchCollectionsStartAsync
} from '../../redux/directory/actions';
import { selectIsCollectionFetching } from '../../redux/directory/selectors';

import Overview from './Overview';
import Collection from './Collection';
import WithSpinner from '../../components/WithSpinner';

const OverViewLoading = WithSpinner(Overview);
const CollectionLoading = WithSpinner(Collection);

const ShopPage = ({ match, fetchCollections, isFetching, setData }) => {
	const [loading, setLoading] = useState(true);

	useEffect(() => {
		fetchCollections();
		console.log(isFetching);
		// const collectionRef = firestore.collection('collections');
		// |------------- SUBSCRIBE TO LIVE DATA -------------|
		// let unsubscribeFromSnapshot = null;
		// unsubscribeFromSnapshot = collectionRef.onSnapshot(async snapshot => {
		// 	const dataCollection = convertCollectionsToMap(snapshot);
		// 	dispatch(setCurrentData(dataCollection));
		// 	setLoading(false);
		// });
		// return () => unsubscribeFromSnapshot();
		// |------------ UNSUBSCRIBE TO LIVE DATA ------------|
		// collectionRef.get().then(snapshot => {
		// 	const dataCollection = convertCollectionsToMap(snapshot);
		// 	setData(dataCollection);
		// 	setLoading(false);
		// });
		// eslint-disable-next-line
	}, []);

	return (
		<Switch>
			<Route
				exact
				path={`${match.path}`}
				render={props => (
					<OverViewLoading isLoading={loading} {...props} />
				)}
			/>
			<Route
				path={`${match.path}/:collectionId`}
				render={props => (
					<CollectionLoading isLoading={loading} {...props} />
				)}
			/>
		</Switch>
	);
};

const mapStateToProps = createStructuredSelector({
	isFetching: selectIsCollectionFetching
});

const mapDispatchToProps = dispatch => ({
	fetchCollections: () => dispatch(fetchCollectionsStartAsync()),
	setData: data => dispatch(setCurrentData(data))
});

export default connect(mapStateToProps, mapDispatchToProps)(ShopPage);
