import { takeLatest, put, all, call } from 'redux-saga/effects';

import userTypes from './types';
import { googleSignInSuccess, googleSignInFailure } from './actions';

import { auth, googleProvider } from '../../firebase/utils';
import createUserProfileDocument from '../../firebase/createUser';

export function* signInWithGoogle() {
	try {
		const { user } = yield auth.signInWithPopup(googleProvider);
		const userRef = yield call(createUserProfileDocument, user);
		const snapshot = yield userRef.get();
		yield googleSignInSuccess(snapshot);
	} catch (error) {
		yield googleSignInFailure(error);
	}
}

export function* onGoogleSignInStart() {
	yield takeLatest(userTypes.GOOGLE_SIGNIN_START, signInWithGoogle);
}

export function* userSagas() {
	yield all([call(onGoogleSignInStart)]);
}
