import cartTypes from './types';

export const setCurrentCart = () => ({
	type: cartTypes.TOGGLE_CART_DROPDOWN
});

export const addToCart = cartItem => ({
	type: cartTypes.ADD_TO_CART,
	payload: cartItem
});
