import React from 'react';
import StripeCheckout from 'react-stripe-checkout';

const StripeButton = ({ price }) => {
	const priceForStripe = price * 100;
	const publishableKey = 'pk_test_CXDPdTpBGh3d5iaS8yHnIdFG00Y0v2XEN0';

	const onToken = token => {
		console.log(token);
		alert('Payment successful');
	};

	return (
		<StripeCheckout
			name="E-commerce"
			ammount={priceForStripe}
			currency="GBP"
			label="Pay now"
			billingAddress
			shippingAddress
			image="https://www.google.com/imgres?imgurl=https%3A%2F%2Fimages.pexels.com%2Fphotos%2F545064%2Fpexels-photo-545064.jpeg%3Fauto%3Dcompress%26cs%3Dtinysrgb%26dpr%3D1%26w%3D500&imgrefurl=https%3A%2F%2Fwww.pexels.com%2Fsearch%2Fmoney%2F&tbnid=w7jiMg_6m760zM&vet=12ahUKEwiwifeayf7oAhUFfRQKHU-iAHgQMygCegUIARCaAg..i&docid=_p-nYIxeiRddtM&w=500&h=334&q=money%20images&ved=2ahUKEwiwifeayf7oAhUFfRQKHU-iAHgQMygCegUIARCaAg"
			description={`Your total is £${price}`}
			panelLabel="Pay now"
			token={onToken}
			stripeKey={publishableKey}
		/>
	);
};

export default StripeButton;
