// .preview {
//   display: flex;
//   justify-content: space-between;
// }
//   }

const CollectionPreview = ({ title, items }) => (
	<Preview>
		<h1 className="title">{title}</h1>
		<div className="preview">
			{items.slice(0, 4).map(({ id, name }) => (
				<div key={id}>{name}</div>
			))}
		</div>
	</Preview>
);

export default CollectionPreview;
