import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';

import { ReactComponent as Logo } from '../../assets/crown.svg';

// .header {
// height: 70px;
// width: 100%;
// display: flex;
// justify-content: space-between;
// margin-bottom: 25px;

//     .logo-container {
//   height: 100%;
//   width: 70px;
//   padding: 25px;
//     }

// .options {
//   width: 50%;
//   height: 100%;
//   display: flex;
//   align-items: center;
//   justify-content: flex-end;

//   .option {
//     padding: 10px 15px;
//   }
// }
//   }

const Container = styled.div`
	height: 70px;
	width: 100%;
	display: flex;
	justify-content: space-between;
	margin-bottom: 25px;
`;

const LogoStyled = styled(Logo)`
	height: 100%;
	width: 70px;
	padding: 25px;
`;

const Options = styled.div`
	.options {
		width: 50%;
		height: 100%;
		display: flex;
		align-items: center;
		justify-content: flex-end;

		.option {
			padding: 10px 15px;
		}
	}
`;

const Header = () => (
	<Container>
		<Link to="/">
			<LogoStyled />
		</Link>
		<Options>
			<Link to="/shop">SHOP</Link>
			<Link to="/contact">CONTACT</Link>
		</Options>
	</Container>
);

export default Header;
