//       font-size: 28px;
//       margin-bottom: 25px;
//     }

//     .preview {
//       display: flex;
//       justify-content: space-between;
//     }
//   }

const CollectionPreview = ({ title, items }) => (
	<div>
		<h1>{title}</h1>
		<div>
			{items.slice(0, 4).map(({ id, name }) => (
				<div key={id}>{name}</div>
			))}
		</div>
	</div>
);

export default CollectionPreview;
